package programs;

public class Replacestring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1 = "how was the day";
		System.out.println("before replacing string :" +str1);
		String str2 = str1.replace("how was the day","today is amazing");
		System.out.println("after replacing string :" +str2);

	}

}
